from API import Token, api_exchenge_keyfrom extensions import ValuesCurrency, APIExceptionfrom telebot import typesimport requestsimport telebotimport tracebackbot = telebot.TeleBot(Token)@bot.message_handler(commands=['start', 'help'])def start_message(message):    help_message = """Правило ввода данных валютной пары в следующем формате | <валюта в наличии>,        <валюта на которую нужно поменять> <количество переводимой валюты> | Пример: USD RUB 100"""    bot.send_message(message.chat.id, help_message)@bot.message_handler(commands=['values'])def values(message):    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)    markup.add(types.KeyboardButton('/values'))    currency = ' || '.join(ValuesCurrency.all_currency(api_exchenge_key))    bot.send_message(message.chat.id, currency, reply_markup=markup)@bot.message_handler(content_types=['text'])def change(message):    try:        if len(message.text.split(" ")) != 3:            raise ValueError("Количество введеных символов должно быть 3!")        base, quote, amount = message.text.split(" ")        request = ValuesCurrency.change(base, quote, amount, api_exchenge_key)    except APIException as e:        bot.reply_to(message, f"Ошибка в команде:\n{e}")    except Exception as e:        traceback.print_tb(e.__traceback__)        bot.reply_to(message, f"Неизвестная ошибка:\n{e}")    else:        bot.reply_to(message, request)try:    bot.polling(none_stop=True)except requests.exceptions.ConnectionError:    pass